client/src/components/StaticChatThread.t.content:
import { cn } from "@/lib/utils";
import type { Message } from "@shared/schema";
import { formatDistanceToNow } from "date-fns";

interface StaticChatThreadProps {
  messages: Message[];
  currentUserId?: string;
  className?: string;
}

export function StaticChatThread({ messages, currentUserId, className }: StaticChatThreadProps) {
  return (
    <div className={cn("space-y-4 p-4", className)} data-testid="chat-thread">
      {messages.length === 0 ? (
        <div className="text-center py-12 text-muted-foreground">
          <p>No messages yet</p>
        </div>
      ) : (
        messages.map((message) => {
          const isOwn = message.senderId === currentUserId;

          return (
            <div
              key={message.id}
              data-testid={`message-${message.id}`}
              className={cn(
                "flex",
                isOwn ? "justify-end" : "justify-start"
              )}
            >
              <div
                className={cn(
                  "max-w-[70%] rounded-lg px-4 py-2 space-y-1",
                  isOwn
                    ? "bg-primary text-primary-foreground"
                    : "bg-muted text-foreground"
                )}
              >
                <p className="text-sm">{message.content}</p>
                {message.createdAt && (
                  <p className={cn(
                    "text-xs",
                    isOwn ? "text-primary-foreground/70" : "text-muted-foreground"
                  )}>
                    {formatDistanceToNow(new Date(message.createdAt), { addSuffix: true })}
                  </p>
                )}
              </div>
            </div>
          );
        })
      )}
    </div>
  );
}

client/src/pages/gp/GPPortal.tsx.content:
import { useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { TabNav, type Tab } from "@/components/TabNav";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { StatusChip } from "@/components/StatusChip";
import { StaticChatThread } from "@/components/StaticChatThread";
import { 
  Inbox, 
  CheckCircle2, 
  MessageSquare, 
  History, 
  User,
  LogOut,
  Clock,
  Loader2
} from "lucide-react";
import { api } from "@/lib/api";
import { getUserFromStorage } from "@/lib/storage";

const tabs: Tab[] = [
  { id: "live", label: "Live Requests", href: "/gp", icon: <Inbox className="h-5 w-5" /> },
  { id: "completed", label: "Completed", href: "/gp", icon: <CheckCircle2 className="h-5 w-5" /> },
  { id: "chats", label: "Chats", href: "/gp", icon: <MessageSquare className="h-5 w-5" /> },
  { id: "history", label: "History", href: "/gp", icon: <History className="h-5 w-5" /> },
  { id: "profile", label: "Profile", href: "/gp", icon: <User className="h-5 w-5" /> },
];

const mockMessages = [
  {
    id: "msg-1",
    consultId: "C-001",
    senderId: "patient-1",
    content: "Hi doctor, I've been experiencing severe headaches",
    createdAt: new Date(Date.now() - 3600000).toISOString()
  },
  {
    id: "msg-2",
    consultId: "C-001",
    senderId: "gp-1",
    content: "I understand. Can you describe when the headaches occur?",
    createdAt: new Date(Date.now() - 3000000).toISOString()
  }
];

export default function GPPortal() {
  const [, setLocation] = useLocation();

  const userData = getUserFromStorage() || {};
  const userId = userData.id;
  const role = userData.role;

  const { data: consults = [], isLoading, error } = useQuery({
    queryKey: ["/api/consults", role, userId],
    queryFn: () => api.consults.listConsults(role, userId),
    enabled: !!userId && !!role
  });

  const handleLogout = () => {
    localStorage.removeItem("mediconnect_user");
    setLocation("/");
  };

  return (
    <div className="min-h-screen bg-background">
      <header className="border-b border-border bg-card">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-foreground">GP Portal</h1>
            <p className="text-sm text-muted-foreground">Dr. Sarah Johnson</p>
          </div>
          <Button 
            variant="ghost" 
            size="sm"
            onClick={handleLogout}
            data-testid="button-logout"
          >
            <LogOut className="h-4 w-4 mr-2" />
            Logout
          </Button>
        </div>
      </header>

      <TabNav tabs={tabs} />

      <main className="container mx-auto px-4 py-8 max-w-7xl">
        {isLoading ? (
          <div className="flex items-center justify-center py-12">
            <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
          </div>
        ) : error ? (
          <Card className="p-12 text-center">
            <p className="text-destructive">Error loading consults: {error.message}</p>
          </Card>
        ) : (
          <div className="grid lg:grid-cols-3 gap-6">
            <div className="lg:col-span-2 space-y-4">
              <div className="flex items-center justify-between">
                <h2 className="text-xl font-semibold text-foreground">Live Consultation Requests</h2>
                <span className="text-sm text-muted-foreground">{consults.length} pending</span>
              </div>

              {consults.map((consult) => (
              <Card key={consult.id} className="p-6 hover-elevate" data-testid={`consult-${consult.id}`}>
                <div className="space-y-4">
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <h3 className="font-semibold text-foreground">Patient Consult</h3>
                        <StatusChip status={consult.status} />
                      </div>
                      <p className="text-sm text-muted-foreground mb-1">
                        Consult ID: <span className="font-mono">{consult.id}</span>
                      </p>
                      <p className="text-sm text-foreground">{consult.intakeSummary}</p>
                    </div>
                    <div className="text-right text-sm">
                      <div className="flex items-center gap-1 text-muted-foreground">
                        <Clock className="h-3 w-3" />
                        {new Date(consult.createdAt).toLocaleTimeString()}
                      </div>
                    </div>
                  </div>

                  <div className="flex gap-2">
                    <Button size="sm" data-testid={`button-accept-${consult.id}`}>
                      Accept Consult
                    </Button>
                    <Button size="sm" variant="outline" data-testid={`button-view-${consult.id}`}>
                      View Details
                    </Button>
                  </div>
                </div>
              </Card>
              ))}
            </div>

            <div>
              <Card className="p-6">
                <h3 className="font-semibold text-foreground mb-4">Recent Chats (Read-Only)</h3>
                <StaticChatThread messages={mockMessages} currentUserId="gp-1" />
              </Card>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

client/src/components/StatusChip.tsx.content:
import { cn } from "@/lib/utils";
import type { ConsultStatus, PrescriptionStatus, DiagnosticsStatus, ReferralStatus } from "@shared/schema";

type Status = ConsultStatus | PrescriptionStatus | DiagnosticsStatus | ReferralStatus | string;

interface StatusChipProps {
  status: Status;
  className?: string;
}

const statusConfig: Record<string, { label: string; className: string }> = {
  // Consult statuses
  intake: { label: "Intake", className: "bg-blue-100 text-blue-800 dark:bg-blue-900/30 dark:text-blue-300" },
  queued: { label: "Queued", className: "bg-amber-100 text-amber-800 dark:bg-amber-900/30 dark:text-amber-300" },
  in_progress: { label: "In Progress", className: "bg-healthcare-consult/20 text-healthcare-consult dark:bg-healthcare-consult/30" },
  completed: { label: "Completed", className: "bg-healthcare-completed/20 text-healthcare-completed dark:bg-healthcare-completed/30" },

  // Prescription statuses
  active: { label: "Active", className: "bg-healthcare-prescription/20 text-healthcare-prescription dark:bg-healthcare-prescription/30" },
  dispensed: { label: "Dispensed", className: "bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-300" },
  expired: { label: "Expired", className: "bg-gray-100 text-gray-800 dark:bg-gray-900/30 dark:text-gray-300" },

  // Diagnostics statuses
  ordered: { label: "Ordered", className: "bg-healthcare-diagnostics/20 text-healthcare-diagnostics dark:bg-healthcare-diagnostics/30" },
  sample_collected: { label: "Sample Collected", className: "bg-blue-100 text-blue-800 dark:bg-blue-900/30 dark:text-blue-300" },

  // Referral statuses
  proposed: { label: "Proposed", className: "bg-purple-100 text-purple-800 dark:bg-purple-900/30 dark:text-purple-300" },
  accepted: { label: "Accepted", className: "bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-300" },

  // Generic
  awaiting: { label: "Awaiting", className: "bg-amber-100 text-amber-800 dark:bg-amber-900/30 dark:text-amber-300" },
  ready: { label: "Ready", className: "bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-300" },
  collected: { label: "Collected", className: "bg-gray-100 text-gray-800 dark:bg-gray-900/30 dark:text-gray-300" },
};

export function StatusChip({ status, className }: StatusChipProps) {
  const config = statusConfig[status] || {
    label: status.replace(/_/g, ' ').replace(/\b\w/g, (l) => l.toUpperCase()),
    className: "bg-muted text-muted-foreground"
  };

  return (
    <span
      data-testid={`status-${status}`}
      className={cn(
        "inline-flex items-center rounded-full px-3 py-1 text-xs font-medium",
        config.className,
        className
      )}
    >
      {config.label}
    </span>
  );
}

